import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const Board = () => {
	const [title, isTitle] = useState<string>('');
	const [text, isText] = useState<string>('');
	const [file, setFile] = useState<string>('');
	const [previewImage, setPreviewImage] = useState<string>('');
	const [data, setData] = useState<object>({});

	let profile_preview = null;

	const onTitleChange = (e: any) => {
		isTitle(e.target.value);
	};

	const onTextChange = (e: any) => {
		isText(e.target.value);
	};

	const handleFileOnChange = (e: any) => {
		setFile(e.target.files[0]);
	};

	const handleSubmit = (e: any) => {
		e.preventDefault();

		const postData = {
			title: title,
			text: text,
		};

		setData(postData);
	};

	useEffect(() => {}, [title, text]);
	return (
		<Container>
			<Wrapper>
				<form onSubmit={handleSubmit}>
					<WriteTitle>
						<Title
							name="title"
							placeholder="제목을 입력해주세요"
							onChange={onTitleChange}
							value={title}
						/>
					</WriteTitle>
					<WriteText>
						<Text
							name="text"
							placeholder="내용을 입력해주세요"
							onChange={onTextChange}
							value={text}
						/>
					</WriteText>
					{/* 이미지 업로드가 들어갑니다. */}
					{/* <i
						className="far fa-image"
						style={{
							fontSize: '25px',
							marginBottom: '50px',
							marginLeft: '20px',
							cursor: 'pointer',
						}}
					></i> */}
					<FileBox>
						<FileItem>사진 업로드</FileItem>
						<FileInput type="file" id="image_upload"></FileInput>
					</FileBox>
					<Button type="submit">등록하기</Button>
				</form>
			</Wrapper>
		</Container>
	);
};

const Container = styled.div`
	position: relative;
	display: block;
	margin-top: 80px;
	width: 1000px;
	height: 800px;
	border: 1px solid #ddd;
	padding: 20px 20px 20px 20px;
	left: 50%;
	transform: translateX(-50%);
`;

const Wrapper = styled.div`
	backface-visibility: hidden;
`;

const WriteTitle = styled.div``;

const Title = styled.input`
	height: 50px;
	min-width: 950px;
	border: none;
	border-bottom: 1px solid #eee;
	font-size: 38px;
	outline: 0;
	margin-bottom: 50px;
	&::placeholder {
		font-size: 38px;
		font-weight: 300;
		height: 40px;
	}
`;

const WriteText = styled.div``;

const Text = styled.textarea`
	min-height: 505px;
	min-width: 950px;
	border: 1px solid #ddd;
	margin-bottom: 20px;
	font-size: 24px;
	outline: 0;
	overflow: auto;
	&::placeholder {
		font-size: 24px;
		font-weight: 300;
		height: 40px;
	}
`;

const Icon = styled.i``;

const FileBox = styled.div``;

const FileItem = styled.label`
	display: inline-block;
	padding: 0.5em 0.75em;
	color: #fff;
	font-size: inherit;
	line-height: normal;
	vertical-align: middle;
	background-color: #5cb85c;
	cursor: pointer;
	border: 1px solid #4cae4c;
	border-radius: 5px;
	transition: 0.2s;

	&:hover {
		background-color: #6ed36e;
	}
	&:active {
		background-color: #367c36;
	}
`;

const FileInput = styled.input.attrs({ type: 'file' })`
	position: absolute;
	width: 1px;
	height: 1px;
	padding: 0;
	margin: -1px;
	overflow: hidden;
	clip: rect(0, 0, 0, 0);
	border: 0;
`;

const Button = styled.button`
	width: 120px;
	height: 40px;
	font-size: 16px;
	border-radius: 5px;
	background: white;
	border: 1px solid #ddd;
	box-shadow: 0 3px 6px rgba(0, 0, 0, 0.04);
	transition: 0.25s;
	&:hover {
		background: #f2f2f2;
		transition: 0.35s;
	}
	&:focus {
		outline: none;
	}
`;

export default Board;
